package trigram;

import java.util.HashMap;
import java.util.Map;

public class Trigram {

    /*
    ("tokenA", "tokenB") → {
        "tokenC": count,
        "tokenD": count,
        "tokenE": count
    }
    */
    Map<String, Map<String, Integer>> trigramCounts = new HashMap<>();

    public Trigram() {
        // read the corpus.txt file
        String corpus = readFile("corpus.txt");
        String[] words = corpus.split("\\s+");


    }

    private String readFile(String file) {
        try {
            java.nio.file.Path path = java.nio.file.Paths.get("src", "trigram", file);
            return java.nio.file.Files.readString(path, java.nio.charset.StandardCharsets.UTF_8);
        } catch (Exception e) {
            e.printStackTrace();
            return "";
        }
    }


}

package trigram;

import java.io.IOException;
import java.io.InputStream;
import java.nio.charset.StandardCharsets;

public class Utils {

    public static String readFile(String path) {
        try {
            // tentar carregar como resource do classpath (apenas)
            InputStream in = Utils.class.getResourceAsStream(path);
            if (in == null) in = Utils.class.getResourceAsStream("/" + path);
            if (in == null) in = Utils.class.getResourceAsStream("/resources/" + path);
            if (in == null) in = Utils.class.getResourceAsStream("/trigram/" + path);

            if (in != null) {
                try (InputStream is = in) {
                    return new String(is.readAllBytes(), StandardCharsets.UTF_8);
                }
            } else {
                System.err.println("Resource not found in classpath: " + path);
            }

        } catch (IOException e) {
            e.printStackTrace();
        }

        return "";
    }
}
